
angular.module('farmss', ['ionic', 'farmss.services', 'farmss.directives','farmss.controllers'])


angular.module('farmss.controllers', [])

angular.module('farmss.directives', [])

angular.module('farmss')

.run(function($ionicPlatform) {
  $ionicPlatform.ready(function() {
    // Hide the accessory bar by default (remove this to show the accessory bar above the keyboard
    // for form inputs)
    if (window.cordova && window.cordova.plugins.Keyboard) {
      cordova.plugins.Keyboard.hideKeyboardAccessoryBar(true);
    }
    if (window.StatusBar) {
      // org.apache.cordova.statusbar required
      StatusBar.styleDefault();
    }
  });
})

.config(function($ionicConfigProvider){
  $ionicConfigProvider.tabs.position('bottom');
})

.config(function($stateProvider, $urlRouterProvider) {

  $stateProvider

  // setup an abstract state for the tabs directive
  .state('tab', {
    url: "/tab",
    abstract: true,
    templateUrl: "templates/tabs.html"
  })

  // Each tab has its own nav history stack:

  .state('tab.native', {
    url: '/native',
    views: {
      'tab-native': {
        templateUrl: 'templates/native/tab-native.html',
        controller: 'NativeCtrl'
      }
    }
  })

  .state('tab.publishs', {
      url: '/publishs',
      views: {
        'tab-publishs': {
          templateUrl: 'templates/publish/tab-publishs.html',
          controller: 'PublishsCtrl'
        }
      }
    })

  // if none of the above states are matched, use this as the fallback
  $urlRouterProvider.otherwise('/tab/native');
});



angular.module('farmss.controllers')

.controller('NativeCtrl', function($scope) {
    $scope.places = ["坝陵", "半月", "河溶"];
    $scope.types = ["割谷", "耕田", "喷药"];
    $scope.sorts = ["距离最近", "价格最低"];

    $scope.items = [1,2,3];
    $scope.doRefresh = function() {
        $http.get('/new-items')
         .success(function(newItems) {
           $scope.items = newItems;
         })
         .finally(function() {
           // Stop the ion-refresher from spinning
           $scope.$broadcast('scroll.refreshComplete');
         });
     };

})

angular.module('farmss.directives')

.directive('fsDropDown', function(){
    return {
        restrict: "EA",
        transclude: true,
        scope:{
            items: "="
        },
        templateUrl: "./templates/directives/dropDown.html",
        controller: function($scope, $ionicModal){
            $scope.selected = $scope.items[0];
            initModal = function(){
                $ionicModal.fromTemplateUrl("./templates/directives/dropDownModal.html", {
                    scope: $scope,
                    animation: 'slide-in-up'
                }).then(function(modal){
                    $scope.modal = modal;
                });
            };

            initModal();

            $scope.filter = function(){
                if ($scope.modal)
                    $scope.openModal = $scope.modal.show();
            };

            $scope.select = function(item){
                $scope.selected = item;
                $scope.modal.hide();
            };
        }
    };

});
